import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class GlobalService {

  private questions: string[] = [
    ' Quelle est ta couleur préférée et pourquoi ?',
    ' Si tu pouvais voyager n\'importe où dans le monde, où irais-tu ?',
    ' Quel est ton plat préféré à cuisiner ou à manger ?'
  ];

  constructor() { }

  getRandomQuestions(num: number): string[] {
    const question = this.questions.sort(() => 0.5 - Math.random());
    return question.slice(0, num);
  }
}
