import { Component } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { QuestionModalComponent } from './pages/question-modal/question-modal.component';
import { GlobalService } from './services/global.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {

  move = false;

  constructor(public dialog: MatDialog, private globalService: GlobalService) {}

  ngOnInit(): void {
    this.globalService.move$.subscribe(move => {
      this.move = move;
    });
  }

  openModal(enterAnimationDuration?: string, exitAnimationDuration?: string): void {
    this.dialog.open(QuestionModalComponent, {
      width: '250px',
      enterAnimationDuration,
      exitAnimationDuration,
    });
  }

  moveFirewood(): void {
    this.globalService.moveObject();
  }

}
